CREATE TABLE IF NOT EXISTS "users" ("id" text, "email" varchar(320) UNIQUE, "account_type" varchar(16), "store_id" bigint, "name" text, "country" varchar(4), "address" varchar(500), "mobile" varchar(32), "password" bytea, PRIMARY KEY ("id"), UNIQUE ("email"));
CREATE TABLE IF NOT EXISTS "stores" ("id" bigserial, "sub_account_id" varchar(255), "flutterwave_account_id" integer, "rating" real, "business_name" varchar(255), "business_mobile" varchar(32), "business_email" varchar(320), "approved" boolean, "account_bank" varchar(8), "account_number" varchar(32), "country" varchar(4), "logo_id" bigint, "dispatch_rider_id" bigint, PRIMARY KEY ("id"));
CREATE TABLE IF NOT EXISTS "transactions" ("id" text, "status" varchar(16), "type" varchar(16), "customer_id" text, "currency" varchar(8), "amount" text, "store_id" bigint, "date_initiated" timestamptz, "date_resolved" timestamptz, "order_id" bigint, PRIMARY KEY ("id"));
CREATE TABLE IF NOT EXISTS "dispatchriders" ("id" bigserial, "sub_account_id" varchar(255), "flutterwave_account_id" integer, "name" varchar(255), "mobile" varchar(32), "email" varchar(320), "account_bank" varchar(8), "account_number" varchar(32), "country" varchar(4), PRIMARY KEY ("id"));
CREATE TABLE IF NOT EXISTS "images" ("id" bigserial, "link" varchar(320), PRIMARY KEY ("id"));
CREATE TABLE IF NOT EXISTS "product_categories" ("id" serial, "name" varchar(255), "slug" varchar(16) UNIQUE, PRIMARY KEY ("id"), UNIQUE ("slug"));
CREATE TABLE IF NOT EXISTS "products" ("id" bigserial, "title" varchar(255), "stock" integer, "description" text, "price" double precision, "discount" real DEFAULT 0, "delivery_fee" double precision, "display_image_id" bigint, "rating" real, "category_id" integer, "store_id" bigint, "date_listed" timestamptz, PRIMARY KEY ("id"));
CREATE TABLE IF NOT EXISTS "orders" ("id" bigserial, "product_id" bigint, "status" text, "delivery_location" text, "delivery_mobile" text, PRIMARY KEY ("id"));